create database bancoDados;
use bancoDados;

create table receita (
id_receita int auto_increment Primary key,  
nome_receita varchar(45),    
modopreparo varchar (2000),
compatibilidade int,
cont_ingred int
);
create table ingrediente (
	id_ingrediente int auto_increment Primary key,
	nome_ingrediente varchar(45) 
       
   
);
UPDATE receita  SET   compatibilidade=compatibilidade+1 WHERE (nome_receita='abc');
 SELECT nome_receita  FROM receita r where r.compatibilidade>0 order by r.compatibilidade desc;
create table composicao (
	cod_receita int ,
	cod_ingrediente int ,
	quant varchar (45),
	Primary key (cod_receita,cod_ingrediente),
        foreign key (cod_receita) references receita(id_receita),
	foreign key (cod_ingrediente) references ingrediente (id_ingrediente)
    
);





